import React, { useState } from 'react';
import PropTypes from 'prop-types';
import axios from 'axios';

const FileUploader = ({ onDataUpload, onUploadError, onFileChange }) => {
  const [file, setFile] = useState(null); // Add file state

  const handleUpload = async () => {
    try {
      const formData = new FormData();
      formData.append('file', file);

      const response = await axios.post('/api/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      onDataUpload(response.data);
    } catch (error) {
      console.error('Error uploading file:', error);
      onUploadError('An error occurred while uploading the file. Please try again.');
    }
  };

  const handleFileChange = (event) => {
    const selectedFile = event.target.files[0];
    setFile(selectedFile); // Update the file state
    onFileChange(selectedFile); // Pass the selected file to App.js
    onUploadError(null); // Clear any previous error
  };

  return (
    <div>
      <input type="file" accept=".csv,.json" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload</button>
    </div>
  );
};

FileUploader.propTypes = {
  onDataUpload: PropTypes.func.isRequired,
  onUploadError: PropTypes.func.isRequired,
  onFileChange: PropTypes.func.isRequired, // Add this prop type
};

export default FileUploader;
