import React, { useState } from 'react';

function DataTable({ data }) {
  const [currentPage, setCurrentPage] = useState(1);
  const [rowsPerPage] = useState(10);
  const [sortColumn, setSortColumn] = useState(null);
  const [sortOrder, setSortOrder] = useState('asc');
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({});

  // Sorting function for columns
  const sortData = (columnName) => {
    if (sortColumn === columnName) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortColumn(columnName);
      setSortOrder('asc');
    }
  };

  // Filtering function for columns
  const filterData = (columnName, value) => {
    setFilters((prevFilters) => ({ ...prevFilters, [columnName]: value }));
  };

  // Pagination
  const indexOfLastRow = currentPage * rowsPerPage;
  const indexOfFirstRow = indexOfLastRow - rowsPerPage;
  const currentRows = data.slice(indexOfFirstRow, indexOfLastRow);

  return (
    <div className="data-table">
      {/* Search Input */}
      <input
        type="text"
        placeholder="Search..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      {/* Table */}
      <table>
        <thead>
          <tr>
            <th onClick={() => sortData('column1')}>Column 1</th>
            <th onClick={() => sortData('column2')}>Column 2</th>
            {/* Add more column headers */}
          </tr>
        </thead>
        <tbody>
          {currentRows
            .filter((row) =>
              Object.entries(filters).every(([column, value]) => {
                return row[column] === value;
              })
            )
            .filter((row) =>
              Object.values(row).some((value) =>
                value
                  .toString()
                  .toLowerCase()
                  .includes(searchTerm.toLowerCase())
              )
            )
            .sort((a, b) => {
              if (sortColumn) {
                const aValue = a[sortColumn];
                const bValue = b[sortColumn];
                return sortOrder === 'asc'
                  ? aValue.localeCompare(bValue)
                  : bValue.localeCompare(aValue);
              }
              return 0;
            })
            .map((row, index) => (
              <tr key={index}>
                <td>{row.column1}</td>
                <td>{row.column2}</td>
                {/* Render other columns */}
              </tr>
            ))}
        </tbody>
      </table>

      {/* Pagination */}
      <div className="pagination">
        <button
          onClick={() => setCurrentPage(currentPage - 1)}
          disabled={currentPage === 1}
        >
          Previous
        </button>
        <span>Page {currentPage}</span>
        <button
          onClick={() => setCurrentPage(currentPage + 1)}
          disabled={indexOfLastRow >= data.length}
        >
          Next
        </button>
      </div>
    </div>
  );
}

export default DataTable;
