import React from 'react';
import axios from 'axios';
import PropTypes from 'prop-types';

const FileUploader = ({ onFileUpload, onUploadError }) => {
  const handleFileChange = async (event) => {
    const selectedFile = event.target.files[0];

    if (selectedFile) {
      try {
        const formData = new FormData();
        formData.append('file', selectedFile);

        const response = await axios.post('/api/upload', formData, {
          baseURL: 'http://localhost:5555', // Update with your server URL
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        });

        onFileUpload(response.data);
      } catch (error) {
        console.error('Error uploading file:', error);
        onUploadError('An error occurred while uploading the file. Please try again.');
      }
    }
  };

  return (
    <div>
      <input type="file" accept=".csv,.json" onChange={handleFileChange} />
      <button onClick={handleFileChange}>Upload</button>
    </div>
  );
};

FileUploader.propTypes = {
  onFileUpload: PropTypes.func.isRequired,
  onUploadError: PropTypes.func.isRequired,
};

export default FileUploader;
