import React, { useState } from 'react';
import PropTypes from 'prop-types';
import axios from 'axios';

const FileUploader = ({ onDataUpload, onUploadError, onFileChange }) => {
  const [file, setFile] = useState(null);

  const handleFileChange = (event) => {
    const selectedFile = event.target.files[0];
    setFile(selectedFile);
    onFileChange(selectedFile); // Notify parent component about the selected file
    onUploadError(null);
  };

  const handleUpload = async () => {
    if (!file) {
      console.error('Please select a file.');
      onUploadError('Please select a file.');
      return;
    }

    try {
      console.log('Uploading file...');
      const formData = new FormData();
      formData.append('file', file);

      const response = await axios.post('http://localhost:5555/api/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      console.log('File uploaded successfully:', response.data);
      onDataUpload(response.data.file);
    } catch (error) {
      console.error('Error uploading file:', error);
      onUploadError('An error occurred while uploading the file. Please try again.');
    }
  };

  return (
    <div>
      <input type="file" accept=".csv,.json" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload</button>
    </div>
  );
};

FileUploader.propTypes = {
  onDataUpload: PropTypes.func.isRequired,
  onUploadError: PropTypes.func.isRequired,
  onFileChange: PropTypes.func.isRequired,
};

export default FileUploader;
