import React, { useState } from 'react';
import './DataTable.css';
import RowModal from './RowModal'; // Import the RowModal component

function DataTable({ data }) {
  const [isModalOpen, setModalOpen] = useState(false);
  const [selectedRow, setSelectedRow] = useState(null);
  const [filteredData, setFilteredData] = useState(data);
  const [filterText, setFilterText] = useState('');

  const openModal = (rowData) => {
    setSelectedRow(rowData);
    setModalOpen(true);
  };

  const closeModal = () => {
    setSelectedRow(null);
    setModalOpen(false);
  };

  const handleFilterChange = (e) => {
    const searchText = e.target.value;
    setFilterText(searchText);

    const filteredResults = data.filter((row) =>
      Object.values(row)
        .map((value) => String(value).toLowerCase())
        .join(' ')
        .includes(searchText.toLowerCase())
    );

    setFilteredData(filteredResults);
  };

  return (
    <div className="data-table">
      <input
        type="text"
        placeholder="Filter..."
        value={filterText}
        onChange={handleFilterChange}
      />
      <table>
        <thead>
          <tr>
            {/* Render table header */}
            {filteredData.length > 0 &&
              Object.keys(filteredData[0]).map((key, index) => (
                <th key={index}>{key}</th>
              ))}
          </tr>
        </thead>
        <tbody>
          {/* Render table rows */}
          {filteredData.map((rowData, index) => (
            <tr key={index} onClick={() => openModal(rowData)}>
              {Object.values(rowData).map((value, index) => (
                <td key={index}>{value}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>

      {/* Render modal */}
      {isModalOpen && (
        <RowModal rowData={selectedRow} onClose={closeModal} />
      )}
    </div>
  );
}

export default DataTable;
