import React, { useState } from 'react';

function DataTable({ data }) {
  const [currentPage, setCurrentPage] = useState(1);
  const [rowsPerPage] = useState(10);
  const [sortColumn, setSortColumn] = useState(null);
  const [sortOrder, setSortOrder] = useState('asc');
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({});

  const columns = Object.keys(data[0] || {});

  const sortData = (columnName) => {
    if (sortColumn === columnName) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortColumn(columnName);
      setSortOrder('asc');
    }
  };

  const filterData = (columnName, value) => {
    setFilters((prevFilters) => ({ ...prevFilters, [columnName]: value }));
  };

  const indexOfLastRow = currentPage * rowsPerPage;
  const indexOfFirstRow = indexOfLastRow - rowsPerPage;
  const currentRows = data
    .filter((row) => {
      // Apply filters
      for (const key in filters) {
        if (row[key] !== filters[key]) {
          return false;
        }
      }
      return true;
    })
    .filter((row) =>
      columns.some((column) =>
        row[column].toLowerCase().includes(searchTerm.toLowerCase())
      )
    )
    .sort((a, b) => {
      if (sortColumn) {
        const comparison = a[sortColumn].localeCompare(b[sortColumn]);
        return sortOrder === 'asc' ? comparison : -comparison;
      }
      return 0;
    })
    .slice(indexOfFirstRow, indexOfLastRow);

  return (
    <div className="data-table">
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        {/* Add filters for other columns */}
      </div>
      <table>
        <thead>
          <tr>
            {columns.map((column) => (
              <th key={column} onClick={() => sortData(column)}>
                {column}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {currentRows.map((row, index) => (
            <tr key={index}>
              {columns.map((column) => (
                <td key={column}>{row[column]}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
      <div className="pagination">
        <button
          onClick={() => setCurrentPage(currentPage - 1)}
          disabled={currentPage === 1}
        >
          Previous
        </button>
        <span>{currentPage}</span>
        <button
          onClick={() => setCurrentPage(currentPage + 1)}
          disabled={currentRows.length < rowsPerPage}
        >
          Next
        </button>
      </div>
    </div>
  );
}

export default DataTable;
