import React, { useState } from 'react';
import axios from 'axios';

function FileUploader({ onFileUpload, onUploadError }) {
  const [uploadError, setUploadError] = useState(null);

  const handleFileChange = async (event) => {
    const selectedFile = event.target.files[0];

    if (selectedFile) {
      const formData = new FormData();
      formData.append('file', selectedFile);

      try {
        const response = await axios.post('http://localhost:5555/api/upload', formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        });

        // Call the onFileUpload function passed from the parent component
        onFileUpload(response.data.file);

        // Clear the error state
        setUploadError(null);
      } catch (error) {
        // Call the onUploadError function passed from the parent component
        onUploadError(error);
        setUploadError('Error uploading file.');
      }
    }
  };

  return (
    <div className="file-uploader">
      <h2>Upload a CSV File</h2>
      <input type="file" accept=".csv" onChange={handleFileChange} />
      {uploadError && <p className="error">{uploadError}</p>}
    </div>
  );
}

export default FileUploader;
