import React, { useState } from 'react';
import PropTypes from 'prop-types';
import axios from 'axios';

const FileUploader = ({ onFileUpload, onUploadError }) => {
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
    onUploadError(null);
  };

  const handleUpload = async () => {
    try {
      if (!selectedFile) {
        onUploadError('Please select a file to upload.');
        return;
      }

      const formData = new FormData();
      formData.append('file', selectedFile);

      const response = await axios.post('/api/upload', formData, {
        baseURL: 'http://localhost:5555', // Set the correct base URL
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      onFileUpload(response.data);
    } catch (error) {
      console.error('Error uploading file:', error);
      onUploadError('An error occurred while uploading the file. Please try again.');
    }
  };

  return (
    <div>
      <input type="file" accept=".csv,.json" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload</button> {/* Add the Upload button */}
    </div>
  );
};

FileUploader.propTypes = {
  onFileUpload: PropTypes.func.isRequired,
  onUploadError: PropTypes.func.isRequired,
};

export default FileUploader;
